mapper.insert:
INSERT 
  INTO CODE_MST
VALUES (
       #{clsCd}
     , #{clsNm}
     , #{remark}
     , CURRENT_DATE
     , #{id}
     , CURRENT_DATE
     , #{id}
)

INSERT 
  INTO CODE_DTL
VALUES (
       #{codeCd}
     , #{clsCd}
     , #{codeNm}
     , #{opt1}
     , #{opt2}
     , #{opt3}
     , #{remark}
     , CURRENT_DATE
     , #{id}
     , CURRENT_DATE
     , #{id}
)

mapper.delete:
DELETE 
  FROM CODE_DTL 
 WHERE 1=1
   AND CODE_CD = #{codeCd}
   AND CLS_CD =  #{clsCd}

mapper.update:
UPDATE CODE_MST
   SET CLS_NM = #{clsNm}
     , REMARK = #{remark}
     , UPD_DT = CURRENT_DATE
     , UPD_ID = #{id}
 WHERE 1=1
   AND CLS_CD = #{clsCd}

UPDATE CODE_DTL
   SET CODE_NM =  #{codeNm}
     , OPT_1 =   #{opt1}
     , OPT_2 =   #{opt2}
     , OPT_3 =   #{opt3}
     , REMARK =  #{remark}
     , UPD_DT =  CURRENT_DATE
     , UPD_ID =  #{id}
 WHERE 1=1
   AND CLS_CD =  #{clsCd}
   AND CODE_CD = #{codeCd}

mapper.select:
SELECT COUNT(*)
  FROM CODE_DTL
 WHERE 1=1
   AND CLS_CD =  #{clsCd}
   AND CODE_CD = #{codeCd}
